//RegisterForm.js
import axios from 'axios'
import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'

export const RegisterForm = () => {
  let navigate=useNavigate()
  let cpassword=''
    const[err,setErr]= useState({
        isError: false,
        message: '',
    })
    let [errMsg,setErrMsg]=useState()
    let [user,setuser]= useState({
      fullName:'',
      email:'',
      password:''
    })

    let nameHandler=(event)=>{
      setuser({
          ...user,
          fullName:event.target.value
      })
      
  }

    let emailHandler=(event)=>{
        setuser({
            ...user,
            email:event.target.value
        })
        
    }
    
    let passwordHandler=(event)=>{
        setuser({
            ...user,
            password:event.target.value
        })

    }
    let cnfPassswordHandler=(event)=>{
      cpassword=event.target.value;
        if(cpassword!==user.password){
          setErr({
                isError: true,
                message: 'Both the password should be same',
            })
    }}
    /**/
    let formLoginHandler=  (event)=>{
        // let error=''
        
        event.preventDefault()

        axios.post('http://127.0.0.1:3030/user/add',user,{
            headers:{
                'Content-Type':'application/json'

            }
        }).then(response=>{

            navigate('/admin/product/edit')
            console.log(`Register Done!!`);

        })
        .catch(error=>{
            setErrMsg(error.response.data.message)
        })
    }


  return (
    
    <div className='form-container'>
        <h1 className="title"  style={{color:"#E6EBE0", backgroundColor:"#6c757d"}}>Admin Registration</h1>
        
        <form onSubmit={formLoginHandler}>
          <div>
          {err.isError ? <div className="error">{err.message}</div>: null}
          <div className="form-input">
            <input type="name" placeholder="Full Name"  required onChange={nameHandler}></input>
          </div>
          <div className="form-input">
            <input type="email" placeholder="Email Id" required onChange={emailHandler}/>
          </div>
          <div className="form-input">
            <input type="password" placeholder="password"required onChange={passwordHandler}/>
          </div>
          <div className="form-input">
            <input type="password" placeholder="confirm password" required onChange={cnfPassswordHandler}/>
          </div>
          <div className="clearfix"></div>
          <div className="form-input">
                    <button className="btn btn-success">Register</button>
          </div>
          <div className="clearfix"></div>
          {errMsg!==''&&
                <div className='alert alert danger'>{errMsg}</div>
                }
                </div>
        </form>
    </div>
  )
}
