import React, { useContext, useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import AuthContext from "../Store/Auth/AuthContext";
import CartContext from "../Store/CartContext";
import './navbar.css';

export let Navbar = (props) => {
    let AuthCtx = useContext(AuthContext);
    let CartCtx = useContext(CartContext);
    let navigate = useNavigate();
    let [noOfItems, setNoOfItems] = useState(0);

    useEffect(() => {
        setNoOfItems(CartCtx.cartItems.length);
    }, [CartCtx.cartItems]);

    let handleLogOut = () => {
        AuthCtx.setIsLoggedIn(false);
        navigate('/admin/login');
    };

    return (
        <nav className="navbar navbar-expand-lg navbar-dark bg-grey fixed-top">
            <div className="container">
                <Link className="navbar-brand" to="/">
                    <img className='logo' src='https://img.freepik.com/premium-vector/future-font-creative-modern-letter-mark-vector-logo_611044-2.jpg?w=740' alt='LOGO' />
                </Link>

                <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>

                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav ml-auto">
                        <li className="nav-item">
                            <Link className="nav-link" to="/">Home</Link>
                        </li>
                        {!(AuthCtx.isLoggedIn) ? (
                            <>
                                <li className="nav-item">
                                    <Link className="nav-link" to="/admin/login">Login</Link>
                                </li>
                                <li className="nav-item">
                                    <Link className="nav-link" to="/admin/register">Register</Link>
                                </li>
                            </>
                        ) : (
                            <li className="nav-item">
                                <a className="nav-link" onClick={handleLogOut}>Logout</a>
                            </li>
                        )}
                        <li className="nav-item">
                            <Link className="nav-link" to="/admin/product/add">Add Product</Link>
                        </li>
                        <li className="nav-item">
                            <Link className="nav-link" to="/admin/product/edit">Products</Link>
                        </li>
                        <li className="nav-item">
                            <Link className="nav-link" to="/product/cart">
                                <div className="shopping-cart">
                                    <img className="cart" src='https://endlessicons.com/wp-content/uploads/2012/11/shopping-cart-icon-614x460.png' alt='CART' />
                                    <div className="number">{noOfItems}</div>
                                </div>
                            </Link>
                        </li>
                    </ul>
                </div>
            </div>
        </nav>
    );
};
