import  { useState, useEffect } from 'react';
import { CartForm } from './components/cart-form/cart-form';
import { CartList } from './components/Cart-list/CartList';
import {Cart} from './components/cart/Cart';


function App() {
  const [cartArr,setCarts]=useState([]);
  const [newCartAdded, setNewCartAdded] = useState(false);


  useEffect(()=>{
    getCarts();
  },[newCartAdded])

    const getCarts = async() =>{
    const data = await fetch("http://localhost:4001/cart");
    const cart_data = await data.json();
    setCarts(cart_data.cartItem);
  }
  return (
    <div className="App">

      <h1 id='navbar'>List of movies</h1>
      <CartForm OnNewItem = {setNewCartAdded}/>
      <CartList list={cartArr}/>

    </div>

  );
}

export default App;
