import React, { useContext, useEffect } from 'react'
import {Cart} from '../cart/Cart'
import CartContext from '../store/CartContext'

export const CartList = (props) => {
    let CartCtx = useContext(CartContext);   
    useEffect(()=>{
        getCarts();
    },[CartCtx.cartItem])
        let getCarts = async() =>{
        let data = await fetch("http://localhost:4001/");
        let cart_data = await data.json();
        CartCtx.setCarts(cart_data.cartArr);
      } 



  return (
    <div>
      {CartCtx.cartItem.map((cart,index)=>
      <Cart key={index} ProductName={cart.ProductName} ProductImage={cart.ProductImage}  ProductPrice={cart.ProductPrice} 
       ProductRating={cart.ProductRating}/>)
      }

    </div>
  )
}
