//product.js
import React, { useContext } from 'react'
import CartContext from '../Store/CartContext'
import './Product.css'

export let Product =(props)=>{
    // let CartItems=[]
    let CartCtx=useContext(CartContext)
    let addToCartHandler=()=>{
        let cartObj={
            productName:props.productName,
            productImage:props.productImage,
            qty:1,
            productPrice:props.productPrice
        }
        let CartItems=[...CartCtx.cartItems]
        CartItems=CartItems.filter(cartitem=>cartitem.productName===props.productName)
        
        if(CartItems.length>0)
        {
            CartItems[0].qty=CartItems[0].qty+1
        }else{
            CartCtx.setCartItems([...CartCtx.cartItems,cartObj])
        }
        console.log(CartCtx.cartItems);
    }
    return(
       
        <div className='col' style={{}}>
            <div className="card h-100">
                <div className='image' style={{height:'100%'}}>
                <img src={props.productImage} className="card-img" style={{minHeight:'100%'}} alt="..."/>
                </div>
                <div className="card-body">
                    <h5 className="card-title">{props.productName}</h5>
                    <p className="card-text">Rs.{props.productPrice}<br/>{props.productCreatedBy}</p>
                </div>
                <button className='btn btn-secondary' style={{backgroundColor:'#2A3D45'}} onClick={addToCartHandler}>Add to Cart</button>
            </div>
        </div>
    )
}
