//product.js
import React, { useContext } from 'react'
import CartContext from '../Store/CartContext'
import './Product.css'

export let Product =(props)=>{
    // let CartItems=[]
    let CartCtx=useContext(CartContext)
    let addToCartHandler=()=>{
        let cartObj={
            productName:props.productName,
            productImage:props.productImage,
            qty:1,
            productPrice:props.productPrice
        }
        let CartItems=[...CartCtx.cartItems]
        CartItems=CartItems.filter(cartitem=>cartitem.productName===props.productName)
        
        if(CartItems.length>0)
        {
            CartItems[0].qty=CartItems[0].qty+1
        }else{
            CartCtx.setCartItems([...CartCtx.cartItems,cartObj])
        }
        console.log(CartCtx.cartItems);
    }
    return(
       
       

<div class="container page-wrapper">
  <div class="page-inner">
    <div class="row">
      <div class="el-wrapper1">
        <div class="box-up">
          <img class="img" src={props.productImage} alt="..."/>
          <div class="img-info">
            <div class="info-inner">
              <span class="p-name">{props.productName}</span>
              <span class="p-company">{props.productCreatedBy}</span>
            </div>
          </div>
        </div>

        <div class="box-down">
          <div class="h-bg">
            <div class="h-bg-inner"></div>
          </div>

          <a class="cart" href="#">
            <span class="price">Rs.{props.productPrice}</span>
            <span class="add-to-cart">
              <span class="txt">Add in cart</span>
              <button class='txt' onClick={addToCartHandler}>Add to Cart</button>
            </span>
          </a>
        </div>
      </div>
    </div>
  </div>
</div>



    )
}
