import React,{ useState }from "react";
import './form.css';
import axios from 'axios';
import CartContext from '../store/CartContext';
import { Cart } from "../cart/Cart";
export const CartForm = (props)=>{
    let CartCtx = useC
    let [resMsg,setResMsg]= useState('')
 const [formInput,setFormInput] = useState({
        ProductName:'',
        ProductPrice:'',
        ProductImage:'',
        ProductRating:''
 })
    const ProductNameHandler = (event)=>{
        
            setFormInput({
                ...formInput,
                ProductName:event.target.value
            });
    }
    const ProductPriceHandler = (event)=>{
        setFormInput({
            ...formInput, //lost values to undefined
            ProductPrice:event.target.value
        });
    }
    const ProductImageHandler = (event)=>{
        setFormInput({
            ...formInput,
            ProductImage:event.target.value
        });

    }
    const ProductRatingHandler = (event)=>{
        setFormInput({
            ...formInput,
            ProductRating:event.target.value
        });
    }
    const formSubHandler= async(event)=>{
        let error=''
        if(formInput.ProductName==='' && error==='')
        {
            error='Please Enter Product Name'
            setResMsg(error)
        }
        else if (formInput.ProductPrice==='' && error==='')
        {
            error='Please Enter Product Price'
            setResMsg(error)
        }
        else if(formInput.ProductImage==='' && error==='')
        {
            error='Please Enter Product Image'
            setResMsg(error)
        }
        else if(formInput.ProductRating==='' && error==='')
        {
            error='Please Enter Product Creator'
            setResMsg(error)

        }
        else{
            // console.log(formInput);
            saveProductData(formInput)
            // props.onProductAdded(true);
            console.log(`Added to List!!`);
        }
        event.preventDefault()

    }
    const saveProductData= async (formData)=>{
        try{
        let cart ={
            productName:formData.ProductName,
            productPrice:formData.ProductPrice,
            productImage:formData.ProductImage,
            ProductRating:formData.ProductRating
        }
        
            let result = await axios.post("http://localhost:3000/add",Cart,{
                headers:{
                    'Content-Type':'application/json',
                    // 'x-auth-token'
                }
            })
            console.log(result)
            CartCtx.setCarts([...CartCtx.cartItem,cart])
        }
        catch(err){
            console.log('From error',err.message);
        }

    }
    
    return(
        <div className='FormCont'>
            <h2>Add New Movies</h2>
            <div>{resMsg}</div>
            <form onSubmit={formSubHandler}>
                <div className='input'>
                    <input type="text" placeholder='Name' onChange={ProductNameHandler}/>

                </div>
                <div className='input'>
                    <input type="text" placeholder='Price' onChange={ProductPriceHandler}/>
                    
                </div>
                <div className='input'>
                    <input type="text" placeholder='Image' onChange={ProductImageHandler}/>
                    
                </div>
                <div className='input'>
                    <input type="text" placeholder='Rating' onChange={ProductRatingHandler}/>
                    
                </div>
                <div className='input'>
                    <button className='btn-add'>Add Item</button>
                </div>
            </form>
        
        </div>
    )
}
